deepcover:
${nullChecks}
${emptyChecks}
${notNullChecks}
<#list packages as pkg>

	${pkg.nullChecks}
	${pkg.emptyChecks}
	${pkg.notNullChecks}
	<#list pkg.classes as cls>


		${cls.nullChecks}
		${cls.emptyChecks}
		${cls.notNullChecks}
		<#list cls.methods as method>

			${method.nullChecks}
			${method.emptyChecks}
			${method.notNullChecks}
			<#list method.arguments as arg>

				${arg.nullChecks}
				<#if arg.type != 'object'>
				${arg.emptyChecks}
				</#if>
				${arg.notNullChecks}

			</#list>

		</#list>

	</#list>

</#list>
